procedure find_max_min(a, b, c: integer; var max, min: integer);begin
  if a > b then
  begin
    if a > c then
    begin
      max := a;
      if b < c then
        min := b
      else
        min := c;
    end
    else
    begin
      max := c;
      min := b;
    end;
  end
  else
  begin
    if b > c then
    begin
      max := b;
      if a < c then
        min := a
      else
        min := c;
    end
    else
    begin
      max := c;
      if a < b then
        min := a
      else
        min := b;
    end;
  end;
end;

