function calculate_even_sum(numbers: array of Integer; size: Integer): Integer;var
  i, ans: Integer;
begin
  ans := 0;
  for i := 0 to size - 1 do
  begin
    if (numbers[i] mod 2) = 0 then
    begin
      ans := ans + numbers[i];
    end;
  end;
  calculate_even_sum := ans;
end;

