const
  N = 10000;
function countHandshakes(n: Integer; order: array of Integer): Int64;const
  MAX_N = 10000;
var
  ans: Int64;
  tr: array[1..MAX_N] of Integer;
  i, j, x: Integer;
begin
  ans := 0;
  for i := 1 to MAX_N do
    tr[i] := 0;
  for i := 0 to n - 1 do
  begin
    x := order[i] + 1;
    j := x - 1;
    while j > 0 do
    begin
      ans := ans + tr[j];
      j := j - (j and -j);
    end;
    j := x;
    while j <= n do
    begin
      Inc(tr[j]);
      j := j + (j and -j);
    end;
  end;
  countHandshakes := ans;
end;

