function minOperations(n: Integer; pixels: array of Integer): Integer;var
  pre, u: array of Integer;
  f: array of array of int64;
  i, j, len, l: Integer;
begin
  SetLength(pre, n + 1);
  SetLength(u, n + 1);
  SetLength(f, n + 1, n + 1);

  for i := 1 to n do
  begin
    pre[i] := 0;
    u[i] := 0;
  end;

  for i := 1 to n do
  begin
    pre[i] := u[pixels[i - 1]];
    u[pixels[i - 1]] := i;
  end;

  for i := 1 to n do
    for j := 1 to n do
      if i <> j then f[i, j] := 10000000
      else f[i, j] := 0;
  for len := 2 to n do
    for i := 1 to n - len + 1 do
    begin
      j := i + len - 1;
      f[i, j] := f[i, j - 1] + 1;
      l := pre[j];
      while l >= i do
      begin
        if f[i, j] < f[i, l] + f[l + 1, j] then
          f[i, j] := f[i, j]
        else
          f[i, j] := f[i, l] + f[l + 1, j];
        l := pre[l];
      end;
    end;

  minOperations := f[1, n];
end;

