function newModuloFactorial(n, p: QWord): QWord;var
  i: QWord;
  pj: array of QWord;
  ans: QWord;
begin
  SetLength(pj, p);
  pj[0] := 1;
  pj[1] := 1;
  for i := 2 to p - 1 do
    pj[i] := (i * pj[i - 1]) mod p;

  ans := 1;
  while n > 0 do
  begin
    ans := (ans * pj[n mod p]) mod p;
    if (n div p) and 1 = 1 then
      ans := p - ans;
    n := n div p;
  end;
  newModuloFactorial := ans;
end;

