uses
  SysUtils, StrUtils;
function classify_integer(x: string): Integer;
var
  n, i, a, t: Integer;begin
  n := 0;
  for i := 1 to Length(x) do
    n := n + Ord(x[i]) - Ord('0');
  while n > 9 do
  begin
    t := n;
    a := 0;
    while t > 0 do
    begin
      a := a + (t mod 10);
      t := t div 10;
    end;
    n := a;
  end;
  classify_integer := n;
end;

