uses
  SysUtils;
function minRoundsToSameChar(s: AnsiString): Integer;var
  charCount: array[0..25] of Integer;
  i, maxCount: Integer;
begin
  // Initialize character counts
  for i := 0 to 25 do
    charCount[i] := 0;

  // Count the occurrences of each character
  for i := 1 to Length(s) do
    Inc(charCount[Ord(s[i]) - Ord('a')]);

  // Find the number of different characters with non-zero counts
  maxCount := 0;
  for i := 0 to 25 do
    if charCount[i] > 0 then
      Inc(maxCount);

  // The minimum number of rounds required is the count of different
  // characters minus one, because one character does not need to be changed
  minRoundsToSameChar := maxCount - 1;
end;

