     timer = setInterval(function(){
      u.style.left = u.offsetLeft - i + 'px';
      if(i == 1)
      {
       if(u.offsetLeft == -u.offsetWidth / 2)
       {
        u.style.left = 0;
       }
      }

      if(i == -1)
      {
       if(u.offsetLeft == 0)
       {
        u.style.left = -u.offsetWidth / 2 + 'px';
       }
      }
     },30);
