    setTimeout(() => {//延迟弹窗,确定:重玩
     let restart = confirm(win ? `恭喜胜利!\n耗时：${(durtime/10).toFixed(1)}秒` : '挑战失败~');
     if (restart) g_init();
    }, 100);
   }
   function markMine(ev) {//右键标注雷块
    //禁用右键的浏览器默认菜单:阻止默认动作
    ev.preventDefault();
    if (over) return;
