     count++;
     mine_total.value = g[2] - count;
     if (count == g[2]) checkOver();
    } else if (g_info[xy].open == -1) {//如果已标注雷-1, 则标注为疑似雷-2
     g_info[xy].open = -2;
     drawBlock(xy, g_color.mine);
     markText(xy, mine[2]);
     count--;
     mine_total.value = g[2] - count;
    } else if (g_info[xy].open == -2) {//如果标注疑似雷-2, 则恢复未打开状态0
     g_info[xy].open = 0;
     drawBlock(xy, g_color.block);
    }
