canonical_solution:
{
  "TCP_IP": {
    "layers": [
      {
        "layer": "Application Layer",
        "protocols": [
          {
            "name": "HTTP",
            "port": 80,
            "description": "Hypertext Transfer Protocol",
            "secure": {
              "name": "HTTPS",
              "port": 443,
              "description": "HTTP Secure"
            }
          },
          {
            "name": "FTP",
            "port": 21,
            "description": "File Transfer Protocol",
            "secure": {
              "name": "FTPS",
              "port": 990,
              "description": "FTP Secure"
            }
          }
        ]
      },
      {
        "layer": "Transport Layer",
        "protocols": [
          {
            "name": "TCP",
            "port": [20, 21, 80, 443],
            "description": "Transmission Control Protocol",
            "features": {
              "connection_oriented": true,
              "reliable": true,
              "flow_control": true
            }
          },
          {
            "name": "UDP",
            "port": [53, 67, 123],
            "description": "User Datagram Protocol",
            "features": {
              "connection_oriented": false,
              "reliable": false,
              "flow_control": false
            }
          }
        ]
      },
      {
        "layer": "Internet Layer",
        "protocols": [
          {
            "name": "IP",
            "version": "IPv4",
            "description": "Internet Protocol version 4",
            "header_size": "20 bytes",
            "addressing": {
              "format": "dotted-decimal",
              "example": "192.168.1.1"
            }
          },
          {
            "name": "ICMP",
            "description": "Internet Control Message Protocol",
            "uses": [
              "Ping",
              "Traceroute"
            ]
          }
        ]
      },
      {
        "layer": "Link Layer",
        "protocols": [
          {
            "name": "Ethernet",
            "description": "Wired networking technology",
            "frame_size": {
              "min": "64 bytes",
              "max": "1518 bytes"
            }
          },
          {
            "name": "Wi-Fi",
            "description": "Wireless networking technology",
            "standards": [
              "802.11a",
              "802.11b",
              "802.11g",
              "802.11n",
              "802.11ac"
            ]
          }
        ]
      }
    ],
    "common_terms": {
      "latency": "The time it takes for a packet to travel from source to destination.",
      "throughput": "The rate at which data is successfully transmitted over a network.",
      "bandwidth": "The maximum rate of data transfer across a network path."
    }
  }
}

