canonical_solution:
{
  "TCP/IP": {
    "description": "A suite of communication protocols used to interconnect network devices on the internet.",
    "components": [
      {
        "name": "TCP",
        "full_form": "Transmission Control Protocol",
        "function": "Provides reliable, ordered, and error-checked delivery of a stream of bytes between applications running on hosts communicating via an IP network."
      },
      {
        "name": "IP",
        "full_form": "Internet Protocol",
        "function": "Responsible for delivering packets from the source host to the destination host based on the IP addresses in the packet headers."
      }
    ],
    "related_protocols": [
      {
        "name": "HTTP",
        "function": "Hypertext Transfer Protocol, used for transferring web pages and other content over the internet."
      },
      {
        "name": "FTP",
        "function": "File Transfer Protocol, used for the transfer of computer files between a client and server on a computer network."
      },
      {
        "name": "SMTP",
        "function": "Simple Mail Transfer Protocol, used for sending emails across IP networks."
      },
      {
        "name": "DNS",
        "function": "Domain Name System, translates domain names to IP addresses."
      }
    ]
  }
}

