          {"name": "OrderEntry", "function": "Allows staff to enter and manage customer orders."},
          {"name": "ShippingManagement", "function": "Coordinates the logistics of packaging and shipping orders to customers."},
          {"name": "OrderTracking", "function": "Provides updates on order status to customers and store managers."}
        ]
      },
      {
        "name": "CustomerManagement",
        "description": "Maintains customer information and manages communication for promotions and customer service.",
        "features": [
          {"name": "CustomerDatabase", "function": "Stores personal and purchase history information of customers."},
          {"name": "CustomerService", "function": "Handles inquiries, complaints, and returns from customers."},
          {"name": "PromotionManagement", "function": "Manages and targets promotional campaigns to specific customer segments based on buying history."}
