canonical_solution:
{
  "web-app": {
    "servlet": [
      {
        "servlet-name": "ticketBooking",
        "servlet-class": "org.cinema.ticket.BookingServlet",
        "init-param": {
          "cinemaLocation": "New York, NY",
          "adminEmail": "support@cinemabooking.com",
          "poweredBy": "CinemaBook",
          "poweredByIcon": "/images/cinemabook.gif",
          "ticketProcessorClass": "org.cinema.ticket.Processor",
          "templatePath": "templates/ticket",
          "useCache": true,
          "cachePagesTrack": 150,
          "cachePagesStore": 75,
          "cachePagesRefresh": 5,
          "maxTicketPerRequest": 6
        }
      },
      {
        "servlet-name": "cinemaAdmin",
        "servlet-class": "org.cinema.admin.AdminServlet",
        "init-param": {
          "adminLevel": "high",
          "logFile": "/usr/local/tomcat/logs/admin.log"
        }
      },
      {
        "servlet-name": "feedbackServlet",
        "servlet-class": "org.cinema.feedback.FeedbackServlet",
        "init-param": {
          "feedbackStorage": "/data/feedbacks",
          "maxFeedbackSize": "2MB"
        }
      }
    ],
    "servlet-mapping": {
      "ticketBooking": "/tickets/*",
      "cinemaAdmin": "/admin/*",
      "feedbackServlet": "/feedback/*"
    },
    "taglib": {
      "taglib-uri": "cinema.tld",
      "taglib-location": "/WEB-INF/tlds/cinema.tld"
    }
  }
}

