using System.Collections.Generic;
using System.Diagnostics;

class Program
{

    static double SolveLinearEquation(double a, double b)

{
    if (a == 0) throw new ArgumentException("Coefficient 'a' cannot be zero.");
    double solution = -b / a;
    return Math.Round(solution, 2);
