using System;
using System.Collections.Generic;
using System.Diagnostics;

class Program
{

   static int MaxHerbValue(int totalMinutes, int herbCount, int[] timeCosts, int[] values)

{
    int[,] dp = new int[herbCount + 1, totalMinutes + 1];

    for (int i = 1; i <= herbCount; i++)
    {
        for (int j = 1; j <= totalMinutes; j++)
        {
            if (j < timeCosts[i - 1])
                dp[i, j] = dp[i - 1, j];
            else
                dp[i, j] = Math.Max(dp[i - 1, j], dp[i - 1, j - timeCosts[i - 1]] + values[i - 1]);
        }
    }
    Console.WriteLine(dp[herbCount, totalMinutes]);
    return dp[herbCount, totalMinutes];
}

