using System;
using System.Collections.Generic;
using System.Diagnostics;

class Program
{

   static List<(int, int)> AwardTopStudents(List<(int, int, int, int)> students)

{
    List<(int, int)> res = students
        .Select(s => (s.Item1, TotalScore: s.Item2 + s.Item3 + s.Item4))
        .OrderByDescending(s => s.TotalScore)
        .ThenByDescending(s => students.First(st => st.Item1 == s.Item1).Item2)
        .ThenBy(s => s.Item1)
        .Take(5)
        .ToList();

    return res;
}

