using System;
using System.Collections.Generic;
using System.Diagnostics;

class Program
{

static List<(int, int)> FindAmicableNumbers(int limit)

{
    List<(int, int)> amicablePairs = new List<(int, int)>();
    for (int i = 1; i <= limit; i++)
    {
        int sumOfDivisors = SumProperDivisors(i);
        if (sumOfDivisors > i && sumOfDivisors <= limit)
        {
            if (SumProperDivisors(sumOfDivisors) == i)
            {
                amicablePairs.Add((i, sumOfDivisors));
            }
        }
    }
    return amicablePairs;
}

static int SumProperDivisors(int number)
{
    int sum = 0;
    for (int i = 1; i <= number / 2; i++)
    {
        if (number % i == 0)
        {
            sum += i;
        }
    }
    return sum;
}

