using System;
using System.Collections.Generic;
using System.Diagnostics;

class Program
{

    static string IsSequenceJolly(int[] sequence)

{
        if (sequence.Length == 1) return "Jolly";

        int[] differences = new int[sequence.Length - 1];
        for (int i = 0; i < sequence.Length - 1; i++)
        {
            differences[i] = Math.Abs(sequence[i] - sequence[i + 1]);
        }

        Array.Sort(differences);
        for (int i = 0; i < differences.Length; i++)
        {
            if (differences[i] != i + 1) return "Not Jolly";
        }

        return "Jolly";
    }

