using System;
using System.Collections.Generic;
using System.Diagnostics;

class Program
{

   static string EncodeString(string input, int shift)

{
    char[] encodedChars = new char[input.Length];
    for (int i = 0; i < input.Length; i++)
    {
        char letter = input[i];
        if (char.IsLetter(letter))
        {
            char baseChar = char.IsUpper(letter) ? 'A' : 'a';
            encodedChars[i] = (char)(((letter + shift - baseChar) % 26) + baseChar);
        }
        else
        {
            encodedChars[i] = letter;
        }
    }
    return new string(encodedChars);
}

