using System;
using System.Collections.Generic;
using System.Diagnostics;
using System.Numerics;
class Program
{


    public static string StepsToPalindrome(int baseNum, string num)

    {
        const int maxSteps = 30;
        BigInteger originalNumber = ConvertToBase10(num, baseNum);

        if (IsPalindrome(originalNumber, baseNum)){
            return "STEP=0";
        }

        for (int step = 1; step <= maxSteps; step++)
        {
            BigInteger reversed = ReverseNumber(originalNumber, baseNum);
            BigInteger sum = originalNumber + reversed;

            if (IsPalindrome(sum, baseNum))
            {

                Console.WriteLine($"STEP={step}");
                return $"STEP={step}";
            }

            originalNumber = sum;
        }

        return "Impossible!";
    }

    private static BigInteger ConvertToBase10(string number, int baseNum)
    {
        return number
            .Select(c => "0123456789ABCDEF".IndexOf(Char.ToUpper(c)))
            .Aggregate(new BigInteger(0), (current, digit) => current * baseNum + digit);
    }

    private static string ConvertFromBase10(BigInteger number, int baseNum)
    {
        string result = "";
        while (number > 0)
        {
            int remainder = (int)(number % baseNum);
            result = "0123456789ABCDEF"[remainder] + result;
            number /= baseNum;
        }
        return result == "" ? "0" : result;
    }

    private static BigInteger ReverseNumber(BigInteger number, int baseNum)
    {
        string reversedStr = new string(ConvertFromBase10(number, baseNum).Reverse().ToArray());
        return ConvertToBase10(reversedStr, baseNum);
    }

    private static bool IsPalindrome(BigInteger number, int baseNum)
    {
        string numStr = ConvertFromBase10(number, baseNum);
        return numStr.SequenceEqual(numStr.Reverse());
    }

