using System;
using System.Collections.Generic;
using System.Diagnostics;

class Program
{




    public static List<int> MaxProductPartition(int N, int M)

    {
        List<int> partition = new List<int>();

        // If M is greater than N, then we'll have M numbers of 1
        if (M >= N)
        {
            for (int i = 0; i < N; i++)
            {
                partition.Add(1);
            }
            return partition;
        }

        // Otherwise, distribute the sum N among M numbers
        int quotient = N / M;
        int remainder = N % M;

        // Add M-remainder numbers of quotient
        for (int i = 0; i < M - remainder; i++)
        {
            partition.Add(quotient);
        }

        // Add remainder numbers of quotient + 1
        for (int i = 0; i < remainder; i++)
        {
            partition.Add(quotient + 1);
        }

        return partition;
    }

