using System;
using System.Collections.Generic;
using System.Diagnostics;

class Program
{



    static (int, int) FindErrorIds(List<List<int>> ids)

    {
        var allIds = ids.SelectMany(idList => idList).OrderBy(id => id).ToList();
        int missingId = 0, duplicatedId = 0;

        for (int i = 1; i < allIds.Count; i++)
        {
            if (allIds[i] == allIds[i - 1])
            {
                duplicatedId = allIds[i];
            }
            else if (allIds[i] - allIds[i - 1] > 1)
            {
                missingId = allIds[i - 1] + 1;
            }
        }

        return (missingId, duplicatedId);
    }

