function classify_integer(x)
{
    let n = 0;
    for (let i = 0; i < x.length; i++) {
        n += parseInt(x[i], 10);
    }
    while (n > 9) {
        let t = n;
        let a = 0;
        while (t) {
            a += t % 10;
            t = Math.floor(t / 10);
        }
        n = a;
    }
    return n;
}

