function minOperations(n, pixels)
{
    let pre = new Array(n+1).fill(0),
        u = new Array(n+1).fill(0),
        f = Array.from({length: n+1}, () => new Array(n+1).fill(Infinity));

    for (let i = 1; i <= n; i++) {
        pre[i] = u[pixels[i-1]];
        u[pixels[i-1]] = i;
    }

    for (let i = 1; i <= n; i++) {
        f[i][i] = 0;
    }

    for (let len = 2; len <= n; len++) {
        for (let i = 1; i <= n - len + 1; i++) {
            let j = i + len - 1;
            f[i][j] = f[i][j - 1] + 1;
            for (let l = pre[j]; l >= i; l = pre[l]) {
                f[i][j] = Math.min(f[i][j], f[i][l] + f[l + 1][j]);
            }
        }
    }

    return f[1][n];
}

