function expectedCuts(n, m, k)
{
    const MD = 1000000007;
    const N = 1000000;
    let vv = new Array(N * 2 + 2).fill(0);
    let ans = 1;

    vv[1] = 1;
    for (let i = 2; i <= N * 2; i++)
        vv[i] = vv[i - MD % i] * (Math.floor(MD / i) + 1) % MD;
    if (k > n * m) {
        return 0;
    }

    for (let i = Math.floor((k + m - 1) / m); i < n; i++) {
        ans = (ans + vv[i + Math.floor((k + i - 1) / i) - 1]) % MD;
    }
    for (let j = Math.floor((k + n - 1) / n); j < m; j++)
        ans = (ans + vv[Math.floor((k + j - 1) / j) + j - 1]) % MD;

    return ans;
}

