function findLongestConsecutiveOnesLength(n)
{
    let max = 0;
    let ans = 0;
    while (n) {
        if (n & 1)
            ans++;
        else {
            if (ans > max)
                max = ans;
            ans = 0;
        }
        n >>>= 1;
    }
    if (ans > max)
        max = ans;
    return max;
}

