function maxModeSum(n, counts)
{
  let ans = 0;
  let mx = 0;
  let f = new Array(100000).fill(0); // Initialize the array with zeroes

  for (let i = 0; i <= n; ++i) {
    f[i] = 0;
  }

  for (let i = n; i > 0; --i) {
    while (mx < counts[i - 1]) {
      mx++;
      f[mx] = f[mx - 1] + i;
    }
    ans += f[counts[i - 1]];
  }
  return ans;
}

