        if ('A' <= text[i] && text[i] <= 'E') {
            shiftedText += String.fromCharCode(text.charCodeAt(i) + 21); // 'V' - 'A' = 21
        } else if ('F' <= text[i] && text[i] <= 'Z') {
            shiftedText += String.fromCharCode(text.charCodeAt(i) - 5); // 'A' - 'F' = -5
        } else {
            shiftedText += text[i];
        }
    }
    return shiftedText;
}

