/**
 * Given a permutation q of n elements and an integer k, find the number of permutations p of n elements
 * such that f(p) = q, where f(p) is the lexicographically smallest permutation that can be obtained by
 * dividing p into exactly k non-empty contiguous segments and sorting each segment (i.e., choose k-1
 * breakpoints 1 <= x1 < x2 < ... < x(k-1) < n, and divide it into [1, x1], (x1, x2], ..., (x(k-1), n]).
 * The result should be modulo 998244353.
 * 
 * @example
 * countPermutations(2, 1, [1, 2])
 * // returns 2
 */
const countPermutations = (n, k, q) => {

const P = 998244353;
    let dp = Array.from({length: n + 1}, () => new Array(n + 1).fill(0));
    let jc = new Array(n + 1).fill(0);
    let f = new Array(n + 1).fill(0);

    dp[0][0] = f[0] = jc[0] = 1;

    for (let i = 1; i <= n; i++) {
        jc[i] = (jc[i - 1] * i) % P;
    }

    for (let i = 1; i <= n; i++) {
        f[i] = jc[i];
        for (let j = 1; j < i; j++) {
            f[i] = (f[i] + P - (f[j] * jc[i - j]) % P) % P;
        }
    }

    for (let i = 1; i <= n; i++) {
        for (let j = 0; j < i; j++) {
            for (let kk = 1; kk <= n; kk++) {
                dp[i][kk] = (dp[i][kk] + dp[j][kk - 1] * f[i - j]) % P;
            }
        }
    }

    let m = 0;
    for (let i = 1; i < n; i++) {
        if (q[i - 1] > q[i]) {
            m = i;
            break;
        }
    }

    let ans = 0;
    if (m === n || m === 0) {
        for (let i = k; i <= n; i++) {
            ans = (ans + dp[n][i]) % P;
        }
    } else {
        for (let i = m + 1; i <= n; i++) {
            if (i !== m + 1 && (q[i - 2] > q[i - 1] || q[i - 1] < q[m - 1])) {
                break;
            }
            let c = k - 1 + i - n;
            if (c >= 0) {
                ans = (ans + dp[m][c] * jc[i - m - 1]) % P;
            }
        }
    }

    return ans;
};

