/**
 * Determines the number of ways to choose a triplet (a, b, c) 
 * from an array of distinct integers representing heights such that the 
 * greatest common divisor (GCD) of the maximum and minimum values 
 * of the triplet is 1.
 * 
 * @example
 * countTriplets([1, 5, 7])
 * // Returns: 1
 */
function countTriplets(heights)

{
    let count = 0;
    const n = heights.length;
    for (let i = 0; i < n; ++i) {
        for (let j = i + 1; j < n; ++j) {
            for (let k = j + 1; k < n; ++k) {
                const minVal = Math.min(heights[i], heights[j], heights[k]);
                const maxVal = Math.max(heights[i], heights[j], heights[k]);
                if (gcd(minVal, maxVal) === 1) {
                    count++;
                }
            }
        }
    }
    return count;
}

// Helper function to find the greatest common divisor
function gcd(a, b) {
    while (b !== 0) {
        let t = b;
        b = a % b;
        a = t;
    }
    return a;
}

