has_close_elements <- function(numbers, threshold)
{n <- length(numbers)
  for (i in 1:(n-1)) {
    for (j in (i+1):n) {
      if (abs(numbers[i] - numbers[j]) < threshold) {
        return(TRUE)
      }
    }
  }
  return(FALSE)
}

