second_smallest_number <- function(numbers) 
{
    if(length(numbers) < 2) {
        stop("The vector must contain at least two numbers.")
    }
    if(numbers[1] < numbers[2]) {
        smallest <- numbers[1]
        second_smallest <- numbers[2]
    } else {
        smallest <- numbers[2]
        second_smallest <- numbers[1]
    }
    if (length(numbers) >=3){
        for (i in 3:length(numbers)) {
            number <- numbers[i]
            if (number < smallest) {
                second_smallest <- smallest
                smallest <- number
            } else if (number < second_smallest && number != smallest) {
                second_smallest <- number
            }
        }
    }
    return(second_smallest)
}

