teach_cooking_skills <- function(member_teaching_preferences) 
{
    n <- length(member_teaching_preferences)
    # Initialize the path matrix
    path <- matrix(0, n, n)
    # Fill in the direct teaching preferences
    for (i in 1:n) {
        for (teachable in member_teaching_preferences[[i]]) {
            path[i, teachable] <- 1
        }
    }
    # Apply the Floyd-Warshall algorithm to find transitive closure
    for (i in 1:n) {
        for (j in 1:n) {
            for (k in 1:n) {
                if (path[i, k] == 1 && path[k, j] == 1) {
                    path[i, j] <- 1
                }
            }
        }
    }
    # Determine the minimum number of direct teachings needed
    direct_teachings_needed <- rep(TRUE, n)
