find_palindromic_numbers <- function(sum_digits) 
{
    result <- c()
    # Check for five-digit numbers
    for (i in 1:9) {
        for (j in 0:9) {
            for (k in 0:9) {
                if (i + j + k + j + i == sum_digits) {
                    result <- c(result, as.numeric(paste0(i, j, k, j, i)))
                }
            }
        }
    }
    # Check for six-digit numbers
    for (i in 1:9) {
        for (j in 0:9) {
            for (k in 0:9) {
                if (i + j + k + k + j + i == sum_digits) {
                    result <- c(result, as.numeric(paste0(i, j, k, k, j, i)))
                }
            }
        }
    }
    return(result)
}

