detect_anagram_keywords <- function(keywords, sentence) 
{
    sorted_keywords <- sapply(keywords, function(keyword) paste(sort(unlist(strsplit(keyword, ""))), collapse = ""))
    words <- unlist(strsplit(sentence, " "))
    found_keywords <- c()
    for (word in words) {
        sorted_word <- paste(sort(unlist(strsplit(word, ""))), collapse = "")
        if (sorted_word %in% sorted_keywords) {
            original_keyword <- keywords[sorted_keywords == sorted_word]
            found_keywords <- c(found_keywords, original_keyword)
        }
    }
    return(found_keywords)
}

