longest_common_subsequence <- function(string1, string2) 
{
    if (nchar(string1) == 0 ||  nchar(string2) == 0){
        return (0)
    }
    len1 <- nchar(string1)
    len2 <- nchar(string2)
    dp_matrix <- matrix(0, nrow = len1 + 1, ncol = len2 + 1)
    for (i in 1:len1) {
        for (j in 1:len2) {
            if (substring(string1, i, i) == substring(string2, j, j)) {
                dp_matrix[i + 1, j + 1] <- dp_matrix[i, j] + 1
            } else {
                dp_matrix[i + 1, j + 1] <- max(dp_matrix[i + 1, j], dp_matrix[i, j + 1])
            }
        }
    }
    return(dp_matrix[len1 + 1, len2 + 1])
}

