maximize_shortest_hop <- function(L, N, M, distances) 
{
    distances <- c(0, sort(distances), L) # Add start and end points
    left <- 0
    right <- L
    best_distance <- 0
    while (left <= right) {
        mid <- (left + right) %/% 2
        last <- 1 # Start from the first stone (including the start point)
        removed <- 0
        for (i in 2:(N+2)) { # Iterate through all stones including the end point
            if (distances[i] - distances[last] < mid) {
                removed <- removed + 1
            } else {
                last <- i
            }
        }
        if (removed <= M) {
            best_distance <- mid
            left <- mid + 1
        } else {
            right <- mid - 1
        }
    }
    return(best_distance)
}

