knight_tour_paths <- function(n, m, sta_x, sta_y) 
{
  # Define the moves of a knight in terms of row and column changes
  dx <- c(1, 1, -2, -1, -2, -1, 2, 2)
  dy <- c(2, -2, 1, 2, -1, -2, 1, -1)
  count = 0
  # Initialize the board with FALSE indicating unvisited positions
  f <- matrix(0, nrow = n, ncol = m)
  # Function to perform DFS
  dfs <- function(x, y, sum) {
    if (sum == n * m) {
      count <<- count+1
    } 
    else {
      for (i in 1:8) {
        xx <- x + dx[i]
        yy <- y + dy[i]
        if (xx >= 1 && xx <= n && yy >= 1 && yy <= m && f[xx, yy] == 0) {
          f[xx, yy] <<- 1
          dfs(xx, yy, sum + 1)
          f[xx, yy] <<- 0
        }
      }
    }
  }
  # Mark the starting position as visited and start DFS
  f[sta_x, sta_y] <- 1  # Adjusting for 1-based indexing in R
  dfs(sta_x, sta_y, 1)
  return(count)
