minimum_cost_path <- function(grid) 
{
  # Get the dimensions of the grid
  n <- nrow(grid)
  # Initialize the cost matrix with maximum possible values
  cost_matrix <- matrix(rep(Inf, n * n), nrow = n)
  # Set the starting point cost
  cost_matrix[1, 1] <- grid[1, 1]
  # Compute the minimum cost path for each cell
  for (i in 1:n) {
    for (j in 1:n) {
      # Update from top cell if not on the first row
      if (i > 1) {
        cost_matrix[i, j] <- min(cost_matrix[i, j], cost_matrix[i-1, j] + grid[i, j])
      }
      # Update from left cell if not on the first column
      if (j > 1) {
        cost_matrix[i, j] <- min(cost_matrix[i, j], cost_matrix[i, j-1] + grid[i, j])
      }
      # Update from bottom cell if not on the last row
      if (i < n) {
        cost_matrix[i+1, j] <- min(cost_matrix[i+1, j], cost_matrix[i, j] + grid[i+1, j])
      }
      # Update from right cell if not on the last column
      if (j < n) {
        cost_matrix[i, j+1] <- min(cost_matrix[i, j+1], cost_matrix[i, j] + grid[i, j+1])
      }
    }
  }
  # Return the cost to reach the bottom-right corner
  return(cost_matrix[n, n])
}

