calculate_string_distance <- function(string1, string2) 
{
    len1 <- nchar(string1)
    len2 <- nchar(string2)
    dp_matrix <- matrix(0, nrow = len1 + 1, ncol = len2 + 1)
    for (i in 1:(len1 + 1)) {
        for (j in 1:(len2 + 1)) {
            if (i == 1) {
                dp_matrix[i, j] <- j - 1
            } else if (j == 1) {
                dp_matrix[i, j] <- i - 1
            } else {
                insert_cost <- dp_matrix[i, j - 1] + 1
                delete_cost <- dp_matrix[i - 1, j] + 1
                sub_cost <- dp_matrix[i - 1, j - 1] + as.integer(substr(string1, i - 1, i - 1) != substr(string2, j - 1, j - 1))
                dp_matrix[i, j] <- min(insert_cost, delete_cost, sub_cost)
            }
        }
    }
    return(dp_matrix[len1 + 1, len2 + 1])
}

