minimum_score_merge <- function(stone_piles) 
{
    n <- length(stone_piles)
    dp <- matrix(0, n, n)
    sum <- cumsum(c(0, stone_piles))
    for (len in 2:n) {
        for (i in 1:(n - len + 1)) {
            j <- i + len - 1
            dp[i, j] <- min(sapply(1:(len - 1), function(k) dp[i, i + k - 1] + dp[i + k, j]))
            dp[i, j] <- dp[i, j] + sum[j + 1] - sum[i]
        }
    }
    return(dp[1, n])
}

