canonical_solution:
shadow_clone_variations <- function(chakra_energy, max_clones) 
{
  # Create a matrix to store intermediate results
  f <- matrix(0, nrow = chakra_energy + 1, ncol = max_clones + 1)
  # Base case: 0 energy with 0 clones
  f[1, 1] <- 1
  # Iterate over each energy level
  for (j in 1:(chakra_energy + 1)) {
    # Iterate over each possible number of clones
    for (k in 2:(max_clones + 1)) {
      # Apply the dynamic programming formula
      f[j, k] <- f[j, k - 1]
      if (j >= k) {
        f[j, k] <- f[j, k] + f[j - k + 1, k]
      }
    }
  }
  # Return the final result
  return(f[chakra_energy + 1, max_clones + 1])
}

