decimal_to_binary <- function(decimal_numbers) 
{
    convert_to_binary <- function(n) {
        if (n == 0) {
            return("0")
        }
        binary <- ""
        is_negative <- n < 0
        n <- abs(n)
        while (n > 0) {
            binary <- paste(n %% 2, binary, sep="")
            n <- n %/% 2
        }
        if (is_negative) {
            binary <- paste("-", binary, sep="")
        }
        return(binary)
    }
    sapply(decimal_numbers, function(n) paste(n, "-->", convert_to_binary(n), sep=''))
}

