longest_consecutive_sequence <- function(numbers) 
{
    if (length(numbers) == 0) return(0)
    if (length(numbers) == 1) return(1)
    numbers <- sort(unique(numbers))  # Sort and remove duplicates
    max_length <- 1
    current_length <- 1
    for (i in 2:length(numbers)) {
        if (numbers[i] == numbers[i-1] + 1) {
            current_length <- current_length + 1
            max_length <- max(max_length, current_length)
        } else {
            current_length <- 1
        }
    }
    return(max_length)
}

