max_product_distribution <- function(N, M) 
{
  # Initialize the vector with 1s to ensure all parts are positive integers
  parts <- rep(1, M)
  # Remaining value to be distributed
  remaining <- N - M
  # Distribute the remaining value as evenly as possible
  while(remaining > 0) {
    for(i in 1:M) {
      if(remaining > 0) {
        parts[i] <- parts[i] + 1
        remaining <- remaining - 1
      } else {
        break
      }
    }
  }
  # To maximize product, distribute the excess above 3 equally
  # This corrects the distribution towards 2s and 3s, maximizing the product
  for(i in 1:M) {
    if(parts[i] > 3 && remaining > 0) {
      parts[i] <- parts[i] - 1
      remaining <- remaining + 1
    }
  }
  while(remaining > 0) {
    for(i in 1:M) {
      if(parts[i] < 3 && remaining > 0) {
        parts[i] <- parts[i] + 1
        remaining <- remaining - 1
      }
    }
  }
  return(sort(parts))
}

