{$ASSERTIONS ON}

uses
  SysUtils;
function hamming_distance(x, y: Integer): Integer;var
  distance: Integer;
  xor_result: Integer;
begin
  distance := 0;
  xor_result := x xor y; 
  // Count the number of bits set in xor_result
  while xor_result <> 0 do
  begin
    if xor_result and 1 = 1 then
      Inc(distance);
    xor_result := xor_result shr 1;
  end;
  hamming_distance := distance;
end;

