uses 
  Math, SysUtils;
function calculate_triangle_area(a, b, c: real): real;
var
  s: real;begin
  if (a + b > c) and (a + c > b) and (b + c > a) then
  begin
    s := (a + b + c) / 2;
    calculate_triangle_area := sqrt(s * (s - a) * (s - b) * (s - c));
  end
  else
    calculate_triangle_area := -1; (* Not a triangle *)
end;

