uses
  SysUtils, Math;
function countTriplets(heights: array of integer; n: integer): integer;var
  i, j, k, minVal, maxVal, a, b, t, count: integer;
begin
  count := 0;
  for i := 0 to n - 1 do
  begin
    for j := i + 1 to n - 1 do
    begin
      for k := j + 1 to n - 1 do
      begin
        minVal := Min(Min(heights[i], heights[j]), heights[k]);
        maxVal := Max(Max(heights[i], heights[j]), heights[k]);
        a := minVal;
        b := maxVal;

        while b <> 0 do
        begin
          t := b;
          b := a mod b;
          a := t;
        end;

        if a = 1 then
          Inc(count);
      end;
    end;
  end;
  countTriplets := count;
end;

