{$ASSERTIONS ON}
function expectedCuts(n, m: Int64; k: Int64): Int64;const
  MD = 1000000007;
var
  vv: array[1..200001] of Int64; 
  i, j: Int64;
  ans: Int64;

begin
  vv[1] := 1;
  for i := 2 to High(vv) do
    vv[i] := (Int64(vv[i - MD mod i]) * (MD div i + 1)) mod MD;

  if k > Int64(n) * m then
  begin
    expectedCuts := 0;
    Exit;
  end;

  ans := 1;
  for i := Int64((k + m - 1) div m) to n - 1 do
    ans := (ans + vv[i + Int64((k + Int64(i) - 1) div Int64(i)) - 1]) mod MD;
  for j := Int64((k + n - 1) div n) to m - 1 do
    ans := (ans + vv[Int64((k + Int64(j) - 1) div Int64(j)) + j - 1]) mod MD;

  expectedCuts := ans;
end;

